  /**
   * Pushes the specified amount of characters back into the input stream.
   *
   * They will be read again by then next call of the scanning method
   *
   * @param number  the number of characters to be read again.
   *                This number must not be greater than yylength()!
   */
  public void yypushback(int number)  {
    if ( number > yylength() )
      zzScanError(ZZ_PUSHBACK_2BIG);

    zzMarkedPos -= number;
  }


  /**
   * Resumes scanning until the next regular expression is matched,
   * the end of input is encountered or an I/O-Error occurs.
   *
   * @return      the next token
   * @exception   java.io.IOException  if any I/O-Error occurs
   */
  public IElementType advance() throws java.io.IOException {
    int zzInput;
    int zzAction;

    // cached fields:
    int zzCurrentPosL;
    int zzMarkedPosL;
    int zzEndReadL = zzEndRead;
    CharSequence zzBufferL = zzBuffer;

    int [] zzTransL = ZZ_TRANS;
    int [] zzRowMapL = ZZ_ROWMAP;
    int [] zzAttrL = ZZ_ATTRIBUTE;

    while (true) {
      zzMarkedPosL = zzMarkedPos;

      zzAction = -1;

      zzCurrentPosL = zzCurrentPos = zzStartRead = zzMarkedPosL;

      zzState = ZZ_LEXSTATE[zzLexicalState];

      // set up zzAction for empty match case:
      int zzAttributes = zzAttrL[zzState];
      if ( (zzAttributes & 1) == 1 ) {
        zzAction = zzState;
      }


      zzForAction: {
        while (true) {

          if (zzCurrentPosL < zzEndReadL) {
            zzInput = Character.codePointAt(zzBufferL, zzCurrentPosL/*, zzEndReadL*/);
            zzCurrentPosL += Character.charCount(zzInput);
          }
          else if (zzAtEOF) {
            zzInput = YYEOF;
            break zzForAction;
          }
          else {
            // store back cached positions
            zzCurrentPos  = zzCurrentPosL;
            zzMarkedPos   = zzMarkedPosL;
            boolean eof = zzRefill();
            // get translated positions and possibly new buffer
            zzCurrentPosL  = zzCurrentPos;
            zzMarkedPosL   = zzMarkedPos;
            zzBufferL      = zzBuffer;
            zzEndReadL     = zzEndRead;
            if (eof) {
              zzInput = YYEOF;
              break zzForAction;
            }
            else {
              zzInput = Character.codePointAt(zzBufferL, zzCurrentPosL/*, zzEndReadL*/);
              zzCurrentPosL += Character.charCount(zzInput);
            }
          }
          int zzNext = zzTransL[ zzRowMapL[zzState] + ZZ_CMAP(zzInput) ];
          if (zzNext == -1) break zzForAction;
          zzState = zzNext;

          zzAttributes = zzAttrL[zzState];
          if ( (zzAttributes & 1) == 1 ) {
            zzAction = zzState;
            zzMarkedPosL = zzCurrentPosL;
            if ( (zzAttributes & 8) == 8 ) break zzForAction;
          }

        }
      }

      // store back cached position
      zzMarkedPos = zzMarkedPosL;

      if (zzInput == YYEOF && zzStartRead == zzCurrentPos) {
        zzAtEOF = true;
        return null;
      }
      else {
        switch (zzAction < 0 ? zzAction : ZZ_ACTION[zzAction]) {
          case 1: 
            { return TwigTokenTypes.CONTENT;
            } 
            // fall through
          case 30: break;
          case 2: 
            { return TwigTokenTypes.INVALID;
            } 
            // fall through
          case 31: break;
          case 3: 
            { return TwigTokenTypes.WHITE_SPACE;
            } 
            // fall through
          case 32: break;
          case 4: 
            { return TwigTokenTypes.OPEN_DICT;
            } 
            // fall through
          case 33: break;
          case 5: 
            { return TwigTokenTypes.CLOSE_DICT;
            } 
            // fall through
          case 34: break;
          case 6: 
            { return TwigTokenTypes.VARIABLE;
            } 
            // fall through
          case 35: break;
          case 7: 
            { // do nothing
            } 
            // fall through
          case 36: break;
          case 8: 
            { return TwigTokenTypes.OPEN_SEXPR;
            } 
            // fall through
          case 37: break;
          case 9: 
            { return TwigTokenTypes.CLOSE_SEXPR;
            } 
            // fall through
          case 38: break;
          case 10: 
            { return TwigTokenTypes.OPEN_LIST;
            } 
            // fall through
          case 39: break;
          case 11: 
            { return TwigTokenTypes.CLOSE_LIST;
            } 
            // fall through
          case 40: break;
          case 12: 
            { return TwigTokenTypes.SEP;
            } 
            // fall through
          case 41: break;
          case 13: 
            { return TwigTokenTypes.FILTER_SEP;
            } 
            // fall through
          case 42: break;
          case 14: 
            { return TwigTokenTypes.EQUALS;
            } 
            // fall through
          case 43: break;
          case 15: 
            { yybegin(statement);
        return TwigTokenTypes.TAG;
            } 
            // fall through
          case 44: break;
          case 16: 
            { 
            } 
            // fall through
          case 45: break;
          case 17: 
            { yybegin(expression);
        return TwigTokenTypes.EXPRESSION_OPEN;
            } 
            // fall through
          case 46: break;
          case 18: 
            { yybegin(statement_block_tag);
        return TwigTokenTypes.STATEMENT_OPEN;
            } 
            // fall through
          case 47: break;
          case 19: 
            { yybegin(comment);
        return TwigTokenTypes.COMMENT_OPEN;
            } 
            // fall through
          case 48: break;
          case 20: 
            { yybegin(YYINITIAL);
        return TwigTokenTypes.EXPRESSION_CLOSE;
            } 
            // fall through
          case 49: break;
          case 21: 
            { return TwigTokenTypes.STRING;
            } 
            // fall through
          case 50: break;
          case 22: 
            { yybegin(YYINITIAL);
        return TwigTokenTypes.STATEMENT_CLOSE;
            } 
            // fall through
          case 51: break;
          case 23: 
            // lookahead expression with fixed lookahead length
            zzMarkedPos = Character.offsetByCodePoints
                (zzBufferL/*, zzStartRead, zzEndRead - zzStartRead*/, zzMarkedPos, -1);
            { return TwigTokenTypes.NUMBER;
            } 
            // fall through
          case 52: break;
          case 24: 
            { return TwigTokenTypes.OPERATOR;
            } 
            // fall through
          case 53: break;
          case 25: 
            { yybegin(YYINITIAL);
        return TwigTokenTypes.COMMENT_CLOSE;
            } 
            // fall through
          case 54: break;
          case 26: 
            { return TwigTokenTypes.TEST;
            } 
            // fall through
          case 55: break;
          case 27: 
            { yypushback(2);
        return TwigTokenTypes.COMMENT_CONTENT;
            } 
            // fall through
          case 56: break;
          case 28: 
            // lookahead expression with fixed base length
            zzMarkedPos = Character.offsetByCodePoints
                (zzBufferL/*, zzStartRead, zzEndRead - zzStartRead*/, zzStartRead, 4);
            { return TwigTokenTypes.BOOLEAN;
            } 
            // fall through
          case 57: break;
          case 29: 
            // lookahead expression with fixed base length
            zzMarkedPos = Character.offsetByCodePoints
                (zzBufferL/*, zzStartRead, zzEndRead - zzStartRead*/, zzStartRead, 5);
            { return TwigTokenTypes.BOOLEAN;
            } 
            // fall through
          case 58: break;
          default:
            zzScanError(ZZ_NO_MATCH);
          }
      }
    }
  }


